-- ----------------------------
-- Table structure for `yqy_article`
-- ----------------------------
DROP TABLE IF EXISTS `yqy_article`;
CREATE TABLE `yqy_article` (
  `aid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增ID',
  `title` char(50) NOT NULL COMMENT '文章标题',
  `create_time` int(15) NOT NULL COMMENT '创建时间',
  `cid` int(2) NOT NULL COMMENT '分类ID',
  `content` text NOT NULL COMMENT '详细内容',
  `author` char(15) NOT NULL DEFAULT '喻庆勇' COMMENT '作者',
  `keywords` char(30) NOT NULL DEFAULT '喻庆勇' COMMENT '关键词',
  `is_show` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否显示1：显示 0：否',
  `is_delete` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否删除1：删除  0：否',
  `sort` int(5) NOT NULL DEFAULT '0' COMMENT '排序',
  `click` tinyint(5) NOT NULL DEFAULT '0' COMMENT '点击数',
  `comment_num` tinyint(3) NOT NULL DEFAULT '0' COMMENT '评论数',
  `description` varchar(250) NOT NULL DEFAULT '0' COMMENT '文章简单描述',
  PRIMARY KEY (`aid`),
  KEY `index_name` (`title`),
  KEY `type` (`cid`,`is_show`,`is_delete`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=162 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of yqy_article
-- ----------------------------

