--

CREATE TABLE IF NOT EXISTS `yqy_users` (
  `uid` int(10) unsigned NOT NULL COMMENT '主键',
  `type` int(1) NOT NULL DEFAULT '1' COMMENT '1：网站注册  2：QQ  ',
  `username` char(100) DEFAULT NULL COMMENT '网站注册用户名',
  `password` varchar(200) DEFAULT NULL COMMENT '网站注册密码',
  `nickname` char(50) NOT NULL DEFAULT '0' COMMENT '第三方名称',
