DROP TABLE IF EXISTS `yqy_admin_user`;
CREATE TABLE `yqy_admin_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `username` char(50) NOT NULL COMMENT '用户名',
  `password` char(50) NOT NULL COMMENT '密码',
  `email` varchar(100) NOT NULL DEFAULT '0' COMMENT '邮箱',
  `status` int(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `last_login_time` int(50) NOT NULL DEFAULT '0' COMMENT '最后登录时间',
  `last_login_ip` char(50) NOT NULL DEFAULT '0' COMMENT '最后登录IP',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

