-- Table structure for `yqy_chat`
-- ----------------------------
DROP TABLE IF EXISTS `yqy_chat`;
CREATE TABLE `yqy_chat` (
  `chid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `create_time` int(30) NOT NULL DEFAULT '0' COMMENT '发布时间',
  `content` text NOT NULL COMMENT '内容',
  `is_show` int(1) NOT NULL DEFAULT '1' COMMENT '是否显示',
  PRIMARY KEY (`chid`),
  KEY `is_show` (`is_show`)
) ENGINE=MyISAM AUTO_INCREMENT=19 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of yqy_chat
-- ----------------------------
INSERT INTO `yqy_chat` VALUES ('3', '1490190112', '怀揣着装逼的心理，写下了这个博客站点！', '1');
INSERT INTO `yqy_chat` VALUES ('4', '1490456136', '之前就用TP3.2写过一个，但是后来学了5.0就浑身不自在，就重写了一遍！', '1');
INSERT INTO `yqy_chat` VALUES ('5', '1490541936', '网站还没上线，一直在愁服务器的事情！', '1');
INSERT INTO `yqy_chat` VALUES ('6', '1490883936', '网站也终于在艰难之下上线了，和朋友们合伙买了个服务器！', '1');
INSERT INTO `yqy_chat` VALUES ('7', '1493735003', 'TP5.0真的是感觉变化很大啊，之前还一直在抗拒使用5.0....现在感觉挺好的', '1');
INSERT INTO `yqy_chat` VALUES ('8', '1494852323', '网上看了很多博客，看见别人都在使用Linux或者MAC，而我还在使用windows，这个苗头不对。。。', '1');
INSERT INTO `yqy_chat` VALUES ('9', '1495255523', '开始学习和练习Linux下的环境搭建和操作', '1');
INSERT INTO `yqy_chat` VALUES ('10', '1496040383', '额，虽然说有一键安装的lamp环境，但还是想装逼的一个个配置啊', '1');
INSERT INTO `yqy_chat` VALUES ('11', '1496118991', '环境也搭建好了，把自己做过的项目放上去试试，还行，能跑', '1');
INSERT INTO `yqy_chat` VALUES ('12', '1496667586', 'Linux确实有很多要学的啊，项目放上去各种问题乍现，难受。。。。', '1');
INSERT INTO `yqy_chat` VALUES ('14', '1499303374', '人生处处充满惊喜，问题一波接一波....', '1');
INSERT INTO `yqy_chat` VALUES ('15', '1499392781', '时间是个好东西啊，问题总能在时间中磨灭', '1');
INSERT INTO `yqy_chat` VALUES ('16', '1499907184', '像小强一样活着，只要不死没有什么不可以的。', '1');
INSERT INTO `yqy_chat` VALUES ('17', '1500019073', '当你感到迷茫的时候，请你抬头看看天边的阳光，看着它是顺着怎样的路找到落山的点；当你感到委屈的时候，请你静心回忆的心房， 看看它装着怎样的喜怒哀乐；当你感到彷徨的时候，请你细心阅读成功的故事。每个人的路都不是一帆风顺的，你该有的辛酸别人也曾有过。想要成功，必须莫畏前路艰辛，放手去博！', '1');
INSERT INTO `yqy_chat` VALUES ('18', '1500629196', '青年的朝气倘已消失，前进不已的好奇心已衰退以后，人生就没有意义了。——穆勒', '1');

-- ----------------------------
-- Table structure for `yqy_comment`
-- ----------------------------
DROP TABLE IF EXISTS `yqy_comment`;
CREATE TABLE `yqy_comment` (
  `cmtid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `uid` int(5) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `type` int(11) NOT NULL DEFAULT '1' COMMENT '评论类型1：文章评论',
  `pid` int(5) NOT NULL DEFAULT '0' COMMENT '父级ID',
  `aid` int(5) NOT NULL DEFAULT '0' COMMENT '文章ID',
  `content` text NOT NULL COMMENT '内容',
  `date` int(20) NOT NULL DEFAULT '0' COMMENT '发布时间',
  `status` int(1) NOT NULL DEFAULT '1' COMMENT '状态1：启用，0：禁用',
  `is_delete` int(1) NOT NULL DEFAULT '0' COMMENT '是否删除1：已删除 0：未删除',
  PRIMARY KEY (`cmtid`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

