-- Records of yqy_comment
-- ----------------------------

-- ----------------------------
-- Table structure for `yqy_demand`
-- ----------------------------
DROP TABLE IF EXISTS `yqy_demand`;
CREATE TABLE `yqy_demand` (
  `xid` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL COMMENT '标题',
  `type` varchar(30) NOT NULL COMMENT '类型',
  `content` text NOT NULL COMMENT '内容',
  `create_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `user_id` int(5) NOT NULL DEFAULT '0',
  `user_name` varchar(40) NOT NULL,
  `see_num` int(10) NOT NULL,
  `comment_num` int(10) NOT NULL,
  `is_show` int(1) NOT NULL DEFAULT '1' COMMENT '是否展示',
  PRIMARY KEY (`xid`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

