-- ----------------------------
-- Table structure for `yqy_users`
-- ----------------------------
DROP TABLE IF EXISTS `yqy_users`;
CREATE TABLE `yqy_users` (
  `uid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `type` int(1) NOT NULL DEFAULT '1' COMMENT '1：网站注册  2：QQ  ',
  `username` char(100) DEFAULT NULL COMMENT '网站注册用户名',
  `password` varchar(200) DEFAULT NULL COMMENT '网站注册密码',
  `nickname` char(50) NOT NULL DEFAULT '0' COMMENT '第三方名称',
  `head_img` varchar(250) NOT NULL DEFAULT '0' COMMENT '第三方头像',
  `openid` varchar(250) NOT NULL DEFAULT '0',
  `create_time` int(20) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `last_login_time` int(20) NOT NULL DEFAULT '0' COMMENT '最后一次登录时间',
  `last_login_ip` varchar(50) NOT NULL DEFAULT '0' COMMENT '最后一次登录Ip',
  `login_times` int(5) NOT NULL DEFAULT '0' COMMENT '登录次数',
  `status` int(1) NOT NULL DEFAULT '1' COMMENT '状态1：启用 0：禁用',
  `email` varchar(100) NOT NULL DEFAULT '0' COMMENT '用户邮箱',
  `is_admin` int(1) NOT NULL DEFAULT '0' COMMENT '是否是管理员 1：是 0：否',
  PRIMARY KEY (`uid`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

