-- ----------------------------
-- Table structure for `yqy_advert`
-- ----------------------------
DROP TABLE IF EXISTS `yqy_advert`;
CREATE TABLE `yqy_advert` (
  `mid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `mname` char(20) NOT NULL DEFAULT '0',
  `from` char(30) NOT NULL DEFAULT '0' COMMENT '类型',
  `img` varchar(200) NOT NULL DEFAULT '0' COMMENT '图片',
  `url` varchar(200) NOT NULL DEFAULT '0' COMMENT '外链',
  `create_time` int(30) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `end_time` int(30) NOT NULL DEFAULT '0' COMMENT '结束时间',
  `is_show` int(1) NOT NULL DEFAULT '1' COMMENT '是否展示1：是  0：否',
  `type` int(1) DEFAULT NULL COMMENT '图片类型',
  PRIMARY KEY (`mid`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

