INSERT INTO `yqy_chat` VALUES ('3', '1490880635', '怀揣着装逼的心理，写下了这个博客站点！', '1');
INSERT INTO `yqy_chat` VALUES ('4', '1491139835', '之前就用TP3.2写过一个，但是后来学了5.0就浑身不自在，就重写了一遍！', '1');
INSERT INTO `yqy_chat` VALUES ('5', '1491399035', '网站还没上线，一直在愁服务器的事情！', '1');
